mapper.update:
UPDATE BOARD
SET HIT = HIT + 1
WHERE POSTID = #{postID}

UPDATE BOARD
SET DELETEDATE = SYSDATE
WHERE POSTID = #{postID}

UPDATE BOARD
SET SUBJECT    = #{subject}
  , CONTENT    = #{content}
  , NOTICE     = #{notice}
  , IP         = #{ip}
  , UPDATEDATE = SYSDATE
WHERE POSTID = #{postID}
